package com.zp.leetcode.package_2022.may.may24;

/**
 * @author zhengpeng
 * @date 2022/5/23 5:46 下午
 * 仅含1的子串数
 **/
public class NumSub {

    public int numSub(String s) {
        long length = s.length();
        char c = '1';
        long sum = 0L;
        long start = 0;
        for (long i = 0L; i < length; i++) {
            final char c1 = s.charAt((int) i);
            if(c != c1){
                sum += getCombination(start,i);
                //起点指向下一个
                start = i + 1;
            }
            //最后一条处理
            if(i == length - 1 && c == c1){
                //若开始下标等于0
                sum += getCombination(start,i + 1);
                final long l = sum % 1000000007;
                return (int) l;
            }
        }
        final long l = sum % 1000000007;
        return (int) l;
    }

    private long getCombination(long start, long end) {
        long diff = end - start;
        long sum = 0L;
        for (long i = 1L; i <= diff; i++) {
            sum += i;
        }
        return sum;
    }

    public static void main(String[] args) {
        //String str = "11101";
        //String str = "111101";
        String str = "";
        //String str = "0110111";
        //String str = "1111";
        NumSub numSub = new NumSub();
        System.out.println(numSub.numSub(str));
    }
}
